import {
  AfterViewInit,
  ApplicationRef,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ElementRef,
  EventEmitter,
  Input,
  OnInit,
  Output,
  ViewChild,
  ViewEncapsulation,
} from "@angular/core";
import { Swiper } from "swiper/types";

@Component({
  selector: "simple-card-console-action",
  templateUrl: "./simple-card.html",
  styleUrls: ["./simple-card.scss"],
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SimpleCardConsoleActionComponent implements OnInit, AfterViewInit {
  @Input() action: any;
  @Input() primaryColor: string = "#30B286";
  @Input() textColor: string = "#FFFFFF";
  @Input() autoScroll? = true;
  @Input() readOnly: boolean = false;
  @Output() onLoadNextAction: EventEmitter<boolean> =
    new EventEmitter<boolean>();
  @Output() onSendReply: EventEmitter<any> = new EventEmitter<any>();
  @Output() onLastActionRendered: EventEmitter<boolean> =
    new EventEmitter<boolean>();

  @ViewChild("swiperCards") swiperCards: ElementRef | undefined;
  swiper?: Swiper;

  public isOver: boolean = false;
  public indexSelectedButton: any;
  public indexHoverButton: number = -1;
  public currentCardIndex: number = 0;
  SWIPER_CONFIG = {
    direction: "horizontal",
    slidesPerView: 1,
    loop: true,
  };

  public message: string;

  constructor(private cdr: ChangeDetectorRef, private app: ApplicationRef) {}

  ngOnInit() {
    //this.store.dispatch(new console_.LoadNextAction({}, this.target));
    this.onLoadNextAction.emit(true);

    if (this.autoScroll) {
      setTimeout(() => {
        let element = document.getElementById("chat-console-messages");
        if (element) {
          element.scrollTop = element.scrollHeight - element.clientHeight;
          this.cdr.markForCheck();
        }
      }, 500);
    }

    if (this.action.cards.length < 3) {
      this.SWIPER_CONFIG.loop = false;
    }
    this.cdr.markForCheck();
  }

  ngAfterViewInit() {
    this.swiper = this.swiperCards?.nativeElement.swiper;

    setTimeout(() => {
      this.onLastActionRendered.emit(true);
      this.cdr.markForCheck();
    }, 0);
  }

  onSwiperIndexChange(event) {
    this.currentCardIndex = event;
    this.cdr.markForCheck();
  }

  /** Move to the next card using right arrow */
  nextCard() {
    if (this.isArrowRightDisabled()) return;
    this.swiper.slideNext(300);
    this.swiper.update();
    this.detectChanges();
  }

  /** Move to the previous card using left arrow */
  previousCard() {
    if (this.isArrowLeftDisabled()) return;
    this.swiper.slidePrev(300);
    this.swiper.update();
    this.detectChanges();
  }

  sendReply(indexSelectedButton, button) {
    if (!this.isOver) {
      this.onSendReply.emit(button);
      this.isOver = true;
      this.indexSelectedButton = indexSelectedButton;
      this.detectChanges();
    }
  }

  openUrl(button) {
    window.open(button.action.url, "_blank");
  }

  openCardUrl(card) {
    if (card.url != "") {
      window.open(card.url, "_blank");
    }
  }

  isArrowRightDisabled() {
    return this.action.cards.length === this.swiper?.activeIndex + 1;
  }

  isArrowLeftDisabled() {
    return this.swiper?.activeIndex <= 0;
  }

  onMouseEnter(i) {
    this.indexHoverButton = i;
    this.detectChanges();
  }

  onMouseLeave() {
    this.indexHoverButton = -1;
    this.detectChanges();
  }

  detectChanges() {
    this.cdr.markForCheck();
    this.app.tick();
  }
}
<div style="margin-top: 20px" class="console-card-type">
  <!-- Left navigation card -->
  <div class="arrow-left" (click)="previousCard()">
    <svg
      *ngIf="action.cards.length > 1"
      [style.background-color]="primaryColor"
      [style.fill]="textColor"
      [ngClass]="{'md-inactive' : isArrowLeftDisabled()}"
      xmlns="http://www.w3.org/2000/svg"
      width="36"
      height="36"
      viewBox="0 0 36 36"
    >
      <path d="M23.12 11.12L21 9l-9 9 9 9 2.12-2.12L16.24 18z" />
      <path d="M0 0h36v36H0z" fill="none" />
    </svg>
  </div>

  <div class="cards-container">
    <swiper-container
      fmSwiper
      id="swiper-cards-console"
      (indexChange)="onSwiperIndexChange($event)"
      [config]="SWIPER_CONFIG"
      init="false"
      #swiperCards
    >
      <swiper-slide
        *ngFor="let card of action.cards; let i = index;"
        class="swiper-slide card-console"
      >
        <div *ngIf="card.imageURL != ''" class="image-preview-container">
          <img
            (click)="openCardUrl(card)"
            style="width: 100%"
            src="{{card.imageURL}}"
          />
        </div>
        <div style="padding-left: 9px; padding-right: 16px">
          <h2
            [ngStyle]="card.url != '' && {'cursor': 'pointer'}"
            (click)="openCardUrl(card)"
            class="card-title"
          >
            {{card.title}}
          </h2>
          <h3 class="card-desc">{{card.subtitle}}</h3>
        </div>

        <div
          style="position: relative; text-align: center"
          *ngFor="let button of card.buttons;let indexButton = index"
        >
          <button
            (mouseenter)="onMouseEnter(indexButton)"
            (mouseleave)="onMouseLeave()"
            [style.border-color]="primaryColor"
            [disabled]="readOnly"
            [style.background-color]="(indexHoverButton==indexButton || indexButton == indexSelectedButton)? primaryColor: null"
            [style.color]="(indexHoverButton==indexButton || indexButton == indexSelectedButton)? textColor: null"
            [ngClass]="{'selected-button': indexButton == indexSelectedButton}"
            *ngIf="button.action.type =='go_to_action'"
            (click)="sendReply(indexButton, button)"
            class="button-card"
          >
            {{button.title}}
          </button>

          <button
            (mouseenter)="onMouseEnter(indexButton)"
            (mouseleave)="onMouseLeave()"
            [style.border-color]="primaryColor"
            [disabled]="readOnly"
            [style.background-color]="(indexHoverButton==indexButton)? primaryColor: null"
            [style.color]="(indexHoverButton==indexButton)? textColor: null"
            *ngIf="button.action.type =='open_url_action'"
            (click)="openUrl(button)"
            class="button-card"
          >
            {{button.title}}
          </button>

          <button
            (mouseenter)="onMouseEnter(indexButton)"
            (mouseleave)="onMouseLeave()"
            [style.border-color]="primaryColor"
            [disabled]="readOnly"
            [style.background-color]="(indexHoverButton==indexButton)? primaryColor: null"
            [style.color]="(indexHoverButton==indexButton)? textColor: null"
            *ngIf="button.action.type =='share_action'"
            class="button-card clustaar-blue-button secondary disabled"
          >
            {{button.title}}
          </button>
        </div>
      </swiper-slide>
    </swiper-container>
  </div>

  <!-- Right navigation card -->
  <div class="arrow-right" (click)="nextCard()">
    <svg
      *ngIf="action.cards.length > 1"
      [style.background-color]="primaryColor"
      [style.fill]="textColor"
      [ngClass]="{'md-inactive' : isArrowRightDisabled()}"
      xmlns="http://www.w3.org/2000/svg"
      width="36"
      height="36"
      viewBox="0 0 36 36"
    >
      <path d="M15 9l-2.12 2.12L19.76 18l-6.88 6.88L15 27l9-9z" />
      <path d="M0 0h36v36H0z" fill="none" />
    </svg>
  </div>
</div>
