import { Target } from '@angular/compiler';
import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';
import { Choice } from '../list/list.model';

@Component({
  selector: 'switch-console-actions',
  templateUrl: './switch.html',
  styleUrls: ['./switch.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})

export class SwitchConsoleActionsComponent {
  @Input() actions: any[] = [];
  @Input() action: any;
  @Input() index: number;
  @Input() inverted = false;
  @Input() primaryColor = '#30B286';
  @Input() textColor = '#FFFFFF';
  @Input() userBubbleColor = '#C5DBEA';
  @Input() userTextColor = '#2C3F59';
  @Input() autoScroll? = true;
  @Input() disabled? = false;
  @Input() showSelectedButton = true;
  @Output() onLoadNextAction: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Output() onLastActionRendered: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Output() onSendReply: EventEmitter<any> = new EventEmitter<any>();
  @Output() onSendEvent: EventEmitter<any> = new EventEmitter<any>();
  @Output() onImageClicked = new EventEmitter<string>();
  @Output() onLinkClicked = new EventEmitter<{ url: string, label: string }>();

  constructor() {
  }

  loadNextAction(): void {
    this.onLoadNextAction.emit(true);
  }

  lastActionRendered(): void {
    this.onLastActionRendered.emit(true);
  }

  sendReply(reply): void {
    this.onSendReply.emit(reply);
  }

  sendEvent(event): void {
    this.onSendEvent.emit(event);
  }
}
<div class="switch-actions-component-console" *ngIf="action" [ngSwitch]="action.type">
  <div *ngSwitchCase="'send_text_action'">
    <text-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                         (onLinkClicked)="onLinkClicked.emit($event)"
                         [action]="action" [primaryColor]="primaryColor"
                         [textColor]="textColor" [indexAction]="index" [inverted]="inverted" [autoScroll]="autoScroll">
    </text-console-action>
  </div>

  <div *ngSwitchCase="'title_message'">
    <title-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                          [action]="action" [indexAction]="index"
                          [autoScroll]="autoScroll">
    </title-console-action>
  </div>

  <div *ngSwitchCase="'support_text_action'">
    <agent-reply-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                                [action]="action" [primaryColor]="primaryColor"
                                [textColor]="textColor" [indexAction]="index" [inverted]="inverted"
                                [autoScroll]="autoScroll">
    </agent-reply-console-action>
  </div>

  <div *ngSwitchCase="'text'">
    <user-message-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                                 [action]="action"
                                 [inverted]="inverted" [autoScroll]="autoScroll" [userBubbleColor]="userBubbleColor"
                                 [userTextColor]="userTextColor">
    </user-message-console-action>
  </div>

  <div *ngSwitchCase="'send_image_action'">
    <image-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                          [action]="action"
                          [primaryColor]="primaryColor" [inverted]="inverted" [autoScroll]="autoScroll"
                          (onImageClicked)="onImageClicked.emit($event)">
    </image-console-action>
  </div>

  <div *ngSwitchCase="'wait_action'">
    <wait-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                         [action]="action"
                         [primaryColor]="primaryColor" [textColor]="textColor" [inverted]="inverted"
                         [autoScroll]="autoScroll">
    </wait-console-action>
  </div>

  <div *ngSwitchCase="'send_quick_replies_action'">
    <quickreply-console-action (onSendReply)="sendReply($event)" (onLoadNextAction)="loadNextAction()"
                               (onLastActionRendered)="lastActionRendered()" [action]="action"
                               [showSelectedButton]="showSelectedButton"
                               [userTextColor]="userTextColor" [userBubbleColor]="userBubbleColor"
                               [primaryColor]="primaryColor" [textColor]="textColor" [inverted]="inverted"
                               [autoScroll]="autoScroll" [indexAction]="index">
    </quickreply-console-action>
  </div>

  <div *ngSwitchCase="'send_cards_action'">
    <card-console-action (onSendReply)="sendReply($event)" (onLoadNextAction)="loadNextAction()"
                         (onLinkClicked)="onLinkClicked.emit($event)"
                         (onLastActionRendered)="lastActionRendered()" [action]="action"
                         [primaryColor]="primaryColor" [textColor]="textColor" [autoScroll]="autoScroll">
    </card-console-action>
  </div>

  <div *ngSwitchCase="'send_simple_cards_action'">
    <simple-card-console-action (onSendReply)="sendReply($event)" (onLoadNextAction)="loadNextAction()"
                                (onLastActionRendered)="lastActionRendered()" [action]="action"
                                [primaryColor]="primaryColor" [textColor]="textColor" [autoScroll]="autoScroll">
    </simple-card-console-action>
  </div>

  <div *ngSwitchCase="'ask_location_action'">
    <location-console-action (onLoadNextAction)="loadNextAction()" (onLastActionRendered)="lastActionRendered()"
                             [action]="action"
                             [primaryColor]="primaryColor" [textColor]="textColor" [inverted]="inverted"
                             [autoScroll]="autoScroll">
    </location-console-action>
  </div>

  <div *ngSwitchCase="'send_js_event_action'">
    <send-js-event-console-action (onSendEvent)="sendEvent($event)" (onLoadNextAction)="loadNextAction()"
                                  [action]="action">
    </send-js-event-console-action>
  </div>

  <div *ngSwitchCase="'send_choices_list_action'">
    <list-action [action]="action" 
                [primaryColor]="primaryColor" 
                [textColor]="textColor" 
                [autoScroll]="autoScroll"
                [userTextColor]="userTextColor" 
                [userBubbleColor]="userBubbleColor"
                [inverted]="inverted"
                [disabled]="disabled"
                (onSendReply)="sendReply($event)" 
                (onLoadNextAction)="loadNextAction()"
                (onLastActionRendered)="lastActionRendered()" 
                >
  </list-action>
  </div>
</div>
