import { ChangeDetectionStrategy, Component } from '@angular/core';
import { DISPLAYED_ACTIONS } from './actions';

@Component({
  selector: 'wc-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AppComponent {
  displayedActions = DISPLAYED_ACTIONS;
  primaryColor = '#660066';
  textColor = 'rgb(161, 161, 243)';
  userBubbleColor = '#30B286';
  userTextColor = '#FFFFFF';

  constructor() {}
}
Demo test:
<div style="max-width:500px;border:1px solid grey; border-radius: 6px;">
  <div *ngFor="let displayedAction of displayedActions; let i = index" id="chat-console-messages">
    <switch-console-actions [actions]="displayedActions" [action]="displayedAction" [index]="i"
                            [inverted]="false"
                            [showSelectedButton]="false"
                            [primaryColor]="primaryColor"
                            [textColor]="textColor"
                            [userTextColor]="userTextColor"
                            [userBubbleColor]="userBubbleColor"></switch-console-actions>
  </div>

</div>
